export default function AdminRegistrationsPage() {
  const { admin } = useAdmin();
  const { toast } = useToast();
  const [registrations, setRegistrations] = useState<Registration[]>([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState<'all' | 'pending' | 'active' | 'waitlist'>('all');

  useEffect(() => {
    loadRegistrations();
  }, [loadRegistrations]);

  async function loadRegistrations() {
    try {
      const data = await registrationService.getAll();
      setRegistrations(data);
    } catch (error) {
      console.error('Error loading registrations:', error);
      toast({
        title: 'Error',
        description: 'Failed to load registrations',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  }

  async function handleStatusUpdate(id: string, status: Registration['status']) {
    try {
      await registrationService.updateStatus(id, status, admin!.id);
      await loadRegistrations();
      toast({
        title: 'Success',
        description: `Registration ${status === 'active' ? 'approved' : 'updated'}`,
      });
    } catch (error) {
      console.error('Error updating status:', error);
      toast({
        title: 'Error',
        description: 'Failed to update registration status',
        variant: 'destructive',
      });
    }
  }

  const columns: ColumnDef<Registration>[] = [
    {
      id: 'select',
      header: ({ table }) => (
        <Checkbox
          checked={table.getIsAllPageRowsSelected()}
          onCheckedChange={(value) => table.toggleAllPageRowsSelected(!!value)}
          aria-label="Select all"
        />
      ),
      cell: ({ row }) => (
        <Checkbox
          checked={row.getIsSelected()}
          onCheckedChange={(value) => row.toggleSelected(!!value)}
          aria-label="Select row"
        />
      ),
      enableSorting: false,
      enableHiding: false,
    },
    {
