import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Skeleton } from '@/components/ui/skeleton';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { useAdmin } from '@/hooks/use-admin';
import { useToast } from '@/hooks/use-toast';
import { db } from '@/lib/firebase';

interface Student {
  id: string;
  displayName: string;
  grade: number;
  school: string;
  guardianId: string;
  guardianName?: string;
  guardianEmail?: string;
  waiverStatus: 'pending' | 'received' | 'rejected';
  waiverDate?: Date;
  createdAt: Date;
}

export default function WaiversPage() {
  const { isAdmin, hasPermission } = useAdmin();
  const { toast } = useToast();
  const [students, setStudents] = useState<Student[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState<'all' | 'pending' | 'received' | 'rejected'>(
    'all'
  );
  const [updating, setUpdating] = useState<string | null>(null);

  useEffect(() => {
    loadStudents();
  }, [loadStudents]);

  async function loadStudents() {
    try {
      // Get all students
      const studentsSnapshot = await getDocs(collection(db, 'students'));
      const studentsData: Student[] = [];

      for (const studentDoc of studentsSnapshot.docs) {
        const data = studentDoc.data();

        // Get guardian info
        let guardianName = '';
        let guardianEmail = '';

        if (data.guardianId) {
          const guardianQuery = query(
            collection(db, 'profiles'),
            where('__name__', '==', data.guardianId)
          );
          const guardianSnapshot = await getDocs(guardianQuery);

          if (!guardianSnapshot.empty) {
            const guardianData = guardianSnapshot.docs[0].data();
            guardianName = guardianData.displayName || guardianData.guardianName || '';
            guardianEmail = guardianData.email || '';
          }
        }

        studentsData.push({
          id: studentDoc.id,
          displayName: data.displayName || `${data.firstName} ${data.lastName}`,
          grade: data.grade,
          school: data.school,
          guardianId: data.guardianId,
          guardianName,
          guardianEmail,
          waiverStatus: data.waiverStatus || 'pending',
          waiverDate: data.waiverDate?.toDate(),
          createdAt: data.createdAt?.toDate() || new Date(),
        });
      }

      // Sort by waiver status (pending first) then by name
      studentsData.sort((a, b) => {
        if (a.waiverStatus === 'pending' && b.waiverStatus !== 'pending') return -1;
        if (a.waiverStatus !== 'pending' && b.waiverStatus === 'pending') return 1;
        return a.displayName.localeCompare(b.displayName);
      });

      setStudents(studentsData);
    } catch (error) {
      console.error('Error loading students:', error);
      toast({
        title: 'Error',
        description: 'Failed to load student waivers',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  }

  async function updateWaiverStatus(
    studentId: string,
    newStatus: 'pending' | 'received' | 'rejected'
  ) {
    setUpdating(studentId);

    try {
      const updateData: any = {
        waiverStatus: newStatus,
      };

      // Add or remove waiver date based on status
      if (newStatus === 'received') {
        updateData.waiverDate = new Date();
      } else {
        updateData.waiverDate = null;
      }

      await updateDoc(doc(db, 'students', studentId), updateData);

      // Update local state
      setStudents(
        students.map((s) =>
          s.id === studentId
            ? {
                ...s,
                waiverStatus: newStatus,
                waiverDate: newStatus === 'received' ? new Date() : undefined,
              }
            : s
        )
      );

      toast({
        title: 'Success',
        description: `Waiver status updated to ${newStatus}`,
      });
    } catch (error) {
      console.error('Error updating waiver status:', error);
      toast({
        title: 'Error',
        description: 'Failed to update waiver status',
        variant: 'destructive',
      });
    } finally {
      setUpdating(null);
    }
  }

  const filteredStudents = students.filter((student) => {
    const matchesSearch =
      student.displayName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      student.guardianName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      student.guardianEmail?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      student.school.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesStatus = statusFilter === 'all' || student.waiverStatus === statusFilter;

    return matchesSearch && matchesStatus;
  });

  const stats = {
    total: students.length,
    pending: students.filter((s) => s.waiverStatus === 'pending').length,
    received: students.filter((s) => s.waiverStatus === 'received').length,
    rejected: students.filter((s) => s.waiverStatus === 'rejected').length,
  };

  if (!hasPermission('manage_users')) {
    return (
      <div className="flex h-full items-center justify-center">
        <p className="text-muted-foreground">You don't have permission to manage waivers.</p>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="space-y-6">
        <Skeleton className="h-8 w-64" />
        <Skeleton className="h-64" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-3xl font-bold">Waiver Management</h1>
        <p className="text-muted-foreground">Track and manage student liability waivers</p>
      </div>

      {/* Stats */}
      <div className="grid gap-4 md:grid-cols-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Students</CardTitle>
            <User className="text-muted-foreground h-4 w-4" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats.total}</div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Pending Waivers</CardTitle>
            <Clock className="h-4 w-4 text-amber-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-amber-600">{stats.pending}</div>
            <p className="text-muted-foreground text-xs">
              {stats.total > 0 ? Math.round((stats.pending / stats.total) * 100) : 0}% of total
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Received</CardTitle>
            <CheckCircle className="h-4 w-4 text-green-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold text-green-600">{stats.received}</div>
            <p className="text-muted-foreground text-xs">
              {stats.total > 0 ? Math.round((stats.received / stats.total) * 100) : 0}% complete
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Rejected</CardTitle>
            <XCircle className="text-destructive h-4 w-4" />
          </CardHeader>
          <CardContent>
            <div className="text-destructive text-2xl font-bold">{stats.rejected}</div>
          </CardContent>
        </Card>
      </div>

      {/* Alert for pending waivers */}
      {stats.pending > 0 && (
        <Alert className="border-amber-200 bg-amber-50">
          <AlertCircle className="h-4 w-4 text-amber-600" />
          <AlertDescription className="text-amber-800">
            <strong>{stats.pending} students</strong> are waiting for waiver approval. Students
            cannot participate in programs until their waiver is received.
          </AlertDescription>
        </Alert>
      )}

      {/* Filters and Search */}
      <Card>
        <CardHeader>
          <CardTitle>Student Waivers</CardTitle>
          <CardDescription>Search and update waiver status for all students</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="mb-4 flex flex-col gap-4 sm:flex-row">
            <div className="relative flex-1">
              <Search className="text-muted-foreground absolute top-1/2 left-3 h-4 w-4 -translate-y-1/2" />
              <Input
                placeholder="Search by student name, guardian, or school..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-9"
              />
            </div>
            <Select value={statusFilter} onValueChange={(value: any) => setStatusFilter(value)}>
              <SelectTrigger className="w-full sm:w-[180px]">
                <SelectValue placeholder="Filter by status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Students</SelectItem>
                <SelectItem value="pending">Pending Only</SelectItem>
                <SelectItem value="received">Received Only</SelectItem>
                <SelectItem value="rejected">Rejected Only</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div className="rounded-md border">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Student</TableHead>
                  <TableHead>Guardian</TableHead>
                  <TableHead>Grade/School</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Waiver Date</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredStudents.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={6} className="text-muted-foreground text-center">
                      No students found
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredStudents.map((student) => (
                    <TableRow key={student.id}>
                      <TableCell>
                        <div className="font-medium">{student.displayName}</div>
                      </TableCell>
                      <TableCell>
                        <div className="space-y-1">
                          <p className="text-sm">{student.guardianName || 'Unknown'}</p>
                          <p className="text-muted-foreground text-xs">{student.guardianEmail}</p>
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="text-sm">
                          <p>Grade {student.grade}</p>
                          <p className="text-muted-foreground text-xs">{student.school}</p>
                        </div>
                      </TableCell>
                      <TableCell>
                        {student.waiverStatus === 'pending' && (
                          <Badge
                            variant="outline"
                            className="border-amber-200 bg-amber-50 text-amber-700"
                          >
                            <Clock className="mr-1 h-3 w-3" />
                            Pending
                          </Badge>
                        )}
                        {student.waiverStatus === 'received' && (
                          <Badge
                            variant="outline"
                            className="border-green-200 bg-green-50 text-green-700"
                          >
                            <CheckCircle className="mr-1 h-3 w-3" />
                            Received
                          </Badge>
                        )}
                        {student.waiverStatus === 'rejected' && (
                          <Badge
                            variant="outline"
                            className="border-red-200 bg-red-50 text-red-700"
                          >
                            <XCircle className="mr-1 h-3 w-3" />
                            Rejected
                          </Badge>
                        )}
                      </TableCell>
                      <TableCell>
                        <div className="text-muted-foreground text-sm">
                          {student.waiverDate ? (
                            <div className="flex items-center gap-1">
                              <Calendar className="h-3 w-3" />
                              {student.waiverDate.toLocaleDateString()}
                            </div>
                          ) : (
                            '-'
                          )}
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="flex gap-2">
                          {student.waiverStatus !== 'received' && (
                            <Button
                              size="sm"
                              variant="outline"
                              className="text-green-600 hover:text-green-700"
                              onClick={() => updateWaiverStatus(student.id, 'received')}
                              disabled={updating === student.id}
                            >
                              <FileCheck className="h-4 w-4" />
                              Mark Received
                            </Button>
                          )}
                          {student.waiverStatus === 'received' && (
                            <Button
                              size="sm"
                              variant="outline"
                              className="text-amber-600 hover:text-amber-700"
                              onClick={() => updateWaiverStatus(student.id, 'pending')}
                              disabled={updating === student.id}
                            >
                              <Clock className="h-4 w-4" />
                              Mark Pending
                            </Button>
                          )}
                          {student.waiverStatus !== 'rejected' && (
                            <Button
                              size="sm"
                              variant="outline"
                              className="text-destructive hover:text-destructive/90"
                              onClick={() => updateWaiverStatus(student.id, 'rejected')}
                              disabled={updating === student.id}
                            >
                              <FileX className="h-4 w-4" />
                              Reject
                            </Button>
                          )}
                        </div>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import { format, formatDistanceToNow } from 'date-fns';
import {
  AlertCircle,
  ArrowLeft,
  CheckCircle,
  Clock,
  Eye,
  EyeOff,
  Info,
  MoreVertical,
  Pin,
  Trash2,
} from 'lucide-react';
import Link from 'next/link';
import { useEffect, useState } from 'react';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from '@/components/ui/alert-dialog';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { Separator } from '@/components/ui/separator';
import { Skeleton } from '@/components/ui/skeleton';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useAuth } from '@/hooks/use-auth';
import { useToast } from '@/hooks/use-toast';
import { type Announcement, announcementService } from '@/lib/firebase-admin';

export default function AnnouncementsPage() {
  const [announcements, setAnnouncements] = useState<Announcement[]>([]);
  const [hiddenAnnouncements, setHiddenAnnouncements] = useState<Announcement[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedAnnouncement, setSelectedAnnouncement] = useState<Announcement | null>(null);
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [announcementToDelete, setAnnouncementToDelete] = useState<Announcement | null>(null);
  const { user } = useAuth();
  const { toast } = useToast();

  useEffect(() => {
    loadAnnouncements();
  }, [loadAnnouncements]);

  async function loadAnnouncements() {
    if (!user) return;

    try {
      // Load visible announcements
      const visibleData = await announcementService.getAll({
        status: 'published',
        userId: user.uid,
        showHidden: false,
      });
      setAnnouncements(visibleData);

      // Load hidden announcements
      const allData = await announcementService.getAll({
        status: 'published',
        userId: user.uid,
        showHidden: true,
      });
      const hidden = allData.filter((a) => a.hiddenBy?.includes(user.uid));
      setHiddenAnnouncements(hidden);

      // Mark as viewed
      if (visibleData.length > 0) {
        visibleData.forEach(async (announcement) => {
          if (!announcement.acknowledgedBy.includes(user.uid)) {
            await announcementService.markAsRead(announcement.id, user.uid);
          }
        });
      }
    } catch (error) {
      console.error('Error loading announcements:', error);
      toast({
        title: 'Error',
        description: 'Failed to load announcements',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  }

  async function handleHide(announcement: Announcement) {
    if (!user) return;

    try {
      await announcementService.hide(announcement.id, user.uid);
      toast({
        title: 'Hidden',
        description: 'Announcement hidden from your view',
      });
      loadAnnouncements();
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to hide announcement',
        variant: 'destructive',
      });
    }
  }

  async function handleUnhide(announcement: Announcement) {
    if (!user) return;

    try {
      await announcementService.unhide(announcement.id, user.uid);
      toast({
        title: 'Restored',
        description: 'Announcement restored to your view',
      });
      loadAnnouncements();
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to restore announcement',
        variant: 'destructive',
      });
    }
  }

  async function handleDelete() {
    if (!announcementToDelete) return;

    try {
      await announcementService.delete(announcementToDelete.id);
      toast({
        title: 'Deleted',
        description: 'Announcement deleted permanently',
      });
      loadAnnouncements();
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to delete announcement',
        variant: 'destructive',
      });
    } finally {
      setDeleteDialogOpen(false);
      setAnnouncementToDelete(null);
      setSelectedAnnouncement(null);
    }
  }

  const getPriorityIcon = (priority: string) => {
    switch (priority) {
      case 'high':
        return <AlertCircle className="h-5 w-5 text-red-500" />;
      case 'normal':
        return <Info className="h-5 w-5 text-blue-500" />;
      default:
        return <Info className="h-5 w-5 text-gray-500" />;
    }
  };

  const getPriorityBadge = (priority: string) => {
    switch (priority) {
      case 'high':
        return <Badge variant="destructive">High Priority</Badge>;
      case 'normal':
        return <Badge>Normal</Badge>;
      default:
        return <Badge variant="secondary">Low</Badge>;
    }
  };

  const AnnouncementCard = ({
    announcement,
    isHidden = false,
  }: {
    announcement: Announcement;
    isHidden?: boolean;
  }) => (
    <Card
      className={`cursor-pointer transition-shadow hover:shadow-lg ${isHidden ? 'opacity-60' : ''}`}
      onClick={() => setSelectedAnnouncement(announcement)}
    >
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between gap-2">
          <div className="flex items-center gap-2">
            {getPriorityIcon(announcement.priority)}
            {announcement.pinned && <Pin className="text-muted-foreground h-4 w-4" />}
          </div>
          <div className="flex items-center gap-2">
            <Badge variant="outline" className="text-xs">
              {announcement.recipients === 'all' ? 'All' : announcement.recipients}
            </Badge>
            <DropdownMenu>
              <DropdownMenuTrigger asChild onClick={(e) => e.stopPropagation()}>
                <Button variant="ghost" size="sm" className="h-8 w-8 p-0">
                  <MoreVertical className="h-4 w-4" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                {isHidden ? (
                  <DropdownMenuItem
                    onClick={(e) => {
                      e.stopPropagation();
                      handleUnhide(announcement);
                    }}
                  >
                    <Eye className="mr-2 h-4 w-4" />
                    Unhide
                  </DropdownMenuItem>
                ) : (
                  <DropdownMenuItem
                    onClick={(e) => {
                      e.stopPropagation();
                      handleHide(announcement);
                    }}
                  >
                    <EyeOff className="mr-2 h-4 w-4" />
                    Hide
                  </DropdownMenuItem>
                )}
                {user && announcement.createdBy === user.uid && (
                  <>
                    <DropdownMenuSeparator />
                    <DropdownMenuItem
                      className="text-destructive"
                      onClick={(e) => {
                        e.stopPropagation();
                        setAnnouncementToDelete(announcement);
                        setDeleteDialogOpen(true);
                      }}
                    >
                      <Trash2 className="mr-2 h-4 w-4" />
                      Delete
                    </DropdownMenuItem>
                  </>
                )}
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </div>
        <CardTitle className="line-clamp-1">{announcement.title}</CardTitle>
        <CardDescription className="text-xs">
          {announcement.publishedAt &&
            formatDistanceToNow(new Date(announcement.publishedAt), { addSuffix: true })}{' '}
          by {announcement.createdByName}
        </CardDescription>
      </CardHeader>
      <CardContent>
        <p className="text-muted-foreground line-clamp-3 text-sm">{announcement.content}</p>
        <div className="text-muted-foreground mt-4 flex items-center gap-4 text-xs">
          <span>{announcement.viewCount} views</span>
          {announcement.acknowledgedBy.includes(user?.uid || '') && (
            <span className="flex items-center gap-1">
              <CheckCircle className="h-3 w-3" /> Read
            </span>
          )}
        </div>
      </CardContent>
    </Card>
  );

  if (loading) {
    return (
      <div className="container mx-auto max-w-6xl space-y-6 p-6">
        <Skeleton className="h-10 w-64" />
        <div className="grid gap-4 md:grid-cols-2">
          {[1, 2, 3, 4].map((i) => (
            <Skeleton key={i} className="h-40" />
          ))}
        </div>
      </div>
    );
  }

  if (selectedAnnouncement) {
    const isOwner = user && selectedAnnouncement.createdBy === user.uid;

    return (
      <div className="container mx-auto max-w-4xl p-6">
        <Button variant="ghost" onClick={() => setSelectedAnnouncement(null)} className="mb-6">
          <ArrowLeft className="mr-2 h-4 w-4" />
          Back to all announcements
        </Button>

        <Card>
          <CardHeader>
            <div className="flex items-start justify-between gap-4">
              <div className="space-y-1">
                <div className="flex items-center gap-2">
                  {selectedAnnouncement.pinned && <Pin className="h-4 w-4" />}
                  <CardTitle className="text-2xl">{selectedAnnouncement.title}</CardTitle>
                </div>
                <CardDescription>
                  By {selectedAnnouncement.createdByName} {' '}
                  {selectedAnnouncement.publishedAt &&
                    format(new Date(selectedAnnouncement.publishedAt), 'MMMM d, yyyy at h:mm a')}
                </CardDescription>
              </div>
              <div className="flex flex-col items-end gap-2">
                {getPriorityBadge(selectedAnnouncement.priority)}
                <Badge variant="outline">
                  {selectedAnnouncement.recipients === 'all'
                    ? 'All Families'
                    : selectedAnnouncement.recipients}
                </Badge>
                {isOwner && (
                  <Button
                    variant="destructive"
                    size="sm"
                    onClick={() => {
                      setAnnouncementToDelete(selectedAnnouncement);
                      setDeleteDialogOpen(true);
                    }}
                  >
                    <Trash2 className="mr-2 h-4 w-4" />
                    Delete
                  </Button>
                )}
              </div>
            </div>
          </CardHeader>
          <CardContent>
            <div className="prose prose-sm max-w-none">
              <p className="whitespace-pre-wrap">{selectedAnnouncement.content}</p>
            </div>
            <Separator className="my-6" />
            <div className="text-muted-foreground flex items-center justify-between text-sm">
              <div className="flex items-center gap-2">
                <CheckCircle className="h-4 w-4" />
                <span>{selectedAnnouncement.viewCount} views</span>
              </div>
              <div className="flex items-center gap-2">
                <Clock className="h-4 w-4" />
                <span>
                  Posted{' '}
                  {selectedAnnouncement.publishedAt &&
                    formatDistanceToNow(new Date(selectedAnnouncement.publishedAt), {
                      addSuffix: true,
                    })}
                </span>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="container mx-auto max-w-6xl space-y-6 p-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Announcements</h1>
          <p className="text-muted-foreground mt-1">
            Stay up to date with all program announcements
          </p>
        </div>
        <Button variant="ghost" asChild>
          <Link href="/dashboard">
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back to Dashboard
          </Link>
        </Button>
      </div>

      <Tabs defaultValue="visible" className="w-full">
        <TabsList>
          <TabsTrigger value="visible">Announcements ({announcements.length})</TabsTrigger>
          <TabsTrigger value="hidden">Hidden ({hiddenAnnouncements.length})</TabsTrigger>
        </TabsList>

        <TabsContent value="visible" className="mt-6">
          {announcements.length === 0 ? (
            <Card>
              <CardContent className="flex flex-col items-center justify-center py-16">
                <Info className="text-muted-foreground mb-4 h-12 w-12" />
                <p className="text-muted-foreground text-lg">No announcements to show</p>
                <p className="text-muted-foreground mt-2 text-sm">
                  Check the hidden tab if you've hidden any announcements
                </p>
              </CardContent>
            </Card>
          ) : (
            <div className="grid gap-4 md:grid-cols-2">
              {announcements.map((announcement) => (
                <AnnouncementCard key={announcement.id} announcement={announcement} />
              ))}
            </div>
          )}
        </TabsContent>

        <TabsContent value="hidden" className="mt-6">
          {hiddenAnnouncements.length === 0 ? (
            <Card>
              <CardContent className="flex flex-col items-center justify-center py-16">
                <EyeOff className="text-muted-foreground mb-4 h-12 w-12" />
                <p className="text-muted-foreground text-lg">No hidden announcements</p>
              </CardContent>
            </Card>
          ) : (
            <div className="grid gap-4 md:grid-cols-2">
              {hiddenAnnouncements.map((announcement) => (
                <AnnouncementCard key={announcement.id} announcement={announcement} isHidden />
              ))}
            </div>
          )}
        </TabsContent>
      </Tabs>

      {/* Delete Confirmation Dialog */}
      <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Announcement</AlertDialogTitle>
            <AlertDialogDescription>
              Are you sure you want to delete "{announcementToDelete?.title}"? This will remove it
              for all users and cannot be undone.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={handleDelete}
              className="bg-destructive text-destructive-foreground"
            >
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

'use client';

import {
  getToken as getAppCheckToken,
  initializeAppCheck,
  ReCaptchaEnterpriseProvider,
} from 'firebase/app-check';
import {
  GoogleAuthProvider,
  getRedirectResult,
  signInWithPopup,
  signInWithRedirect,
} from 'firebase/auth';
import { useEffect, useState } from 'react';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { app, auth } from '@/lib/firebase';

export default function AuthDiagnosticsPage() {
  const [status, setStatus] = useState<string[]>([]);
  const [error, setError] = useState<string>('');
  const [appCheckInfo, setAppCheckInfo] = useState<string>('');

  const addStatus = (message: string) => {
    setStatus((prev) => [...prev, message]);
  };

  useEffect(() => {
    // Check for redirect result
    getRedirectResult(auth)
      .then((result) => {
        if (result) {
          addStatus(' Redirect sign-in successful!');
          addStatus(`User: ${result.user.email}`);
        }
      })
      .catch((error) => {
        if (error.code !== 'auth/popup-blocked-by-browser') {
          addStatus(` Redirect error: ${error.message}`);
        }
      });

    // Log current environment
    addStatus(` Current URL: ${window.location.href}`);
    addStatus(` Auth Domain: ${auth.app.options.authDomain}`);
    addStatus(` Project ID: ${auth.app.options.projectId}`);

    // Try to detect App Check initialization by attempting to get a token
    try {
      addStatus(' Attempting to get App Check token...');
      // Try to initialize App Check if not already initialized
      let appCheck;
      try {
        const siteKey = process.env.NEXT_PUBLIC_RECAPTCHA_ENTERPRISE_SITE_KEY;
        if (siteKey) {
          appCheck = initializeAppCheck(app, {
            provider: new ReCaptchaEnterpriseProvider(siteKey),
            isTokenAutoRefreshEnabled: true,
          });
        }
      } catch (e) {
        // App Check might already be initialized
        addStatus('! App Check may already be initialized');
      }

      // Attempt to get a token without forcing refresh
      if (appCheck) {
        getAppCheckToken(appCheck, false)
          .then((res) => {
            if (res?.token) {
              setAppCheckInfo('App Check token acquired');
              addStatus(' App Check token acquired successfully');
            } else {
              setAppCheckInfo('App Check token not available');
              addStatus('! App Check token not available');
            }
          })
          .catch((e) => {
            setAppCheckInfo(`App Check token error: ${e?.message || e}`);
            addStatus(` App Check token error: ${e?.code || ''} ${e?.message || e}`);
          });
      }
    } catch (e: any) {
      setAppCheckInfo('App Check not initialized');
      addStatus(' App Check not initialized or error accessing it');
    }
  }, [addStatus]);

  const testGooglePopup = async () => {
    try {
      setError('');
      addStatus(' Testing Google sign-in with popup...');

      const provider = new GoogleAuthProvider();
      const result = await signInWithPopup(auth, provider);

      addStatus(' Popup sign-in successful!');
      addStatus(`User: ${result.user.email}`);
      addStatus(`Provider: ${result.providerId}`);
    } catch (error: any) {
      setError(error.message);
      addStatus(` Popup error: ${error.code}`);
      addStatus(`Details: ${error.message}`);

      if (error.code === 'auth/popup-blocked-by-browser') {
        addStatus(' Popup was blocked. Trying redirect...');
        testGoogleRedirect();
      }
    }
  };

  const testGoogleRedirect = async () => {
    try {
      setError('');
      addStatus(' Testing Google sign-in with redirect...');

      const provider = new GoogleAuthProvider();
      await signInWithRedirect(auth, provider);
    } catch (error: any) {
      setError(error.message);
      addStatus(` Redirect error: ${error.code}`);
      addStatus(`Details: ${error.message}`);
    }
  };

  const checkAuthState = () => {
    addStatus(' Checking auth state...');
    addStatus(`Current user: ${auth.currentUser ? auth.currentUser.email : 'None'}`);
    const hasAuthReady = typeof (auth as any).authStateReady === 'function';
    addStatus(`Auth ready method: ${hasAuthReady ? 'Available' : 'Unavailable'}`);
  };

  return (
    <div className="container mx-auto max-w-3xl p-6">
      <Card>
        <CardHeader>
          <CardTitle>Firebase Auth Diagnostics</CardTitle>
          <CardDescription>Comprehensive authentication testing and diagnostics</CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 gap-4 md:grid-cols-3">
            <Button onClick={testGooglePopup} variant="default">
              Test Google Popup
            </Button>
            <Button onClick={testGoogleRedirect} variant="secondary">
              Test Google Redirect
            </Button>
            <Button onClick={checkAuthState} variant="outline">
              Check Auth State
            </Button>
          </div>

          {error && (
            <Alert variant="destructive">
              <AlertDescription>
                <strong>Error:</strong> {error}
              </AlertDescription>
            </Alert>
          )}

          <div className="space-y-2">
            <h3 className="font-semibold">Status Log:</h3>
            <div className="bg-muted h-96 overflow-y-auto rounded-lg p-4">
              {status.map((s, i) => (
                <div key={i} className="py-1 font-mono text-sm">
                  {s}
                </div>
              ))}
            </div>
          </div>

          <Alert>
            <AlertDescription>
              <strong>Common Issues:</strong>
              <ul className="mt-2 list-inside list-disc space-y-1">
                <li>OAuth consent screen not configured</li>
                <li>Domain not in authorized domains list</li>
                <li>Google provider not enabled in Firebase</li>
                <li>API restrictions blocking auth</li>
              </ul>
            </AlertDescription>
          </Alert>

          <div className="bg-muted rounded-lg p-4">
            <h4 className="mb-2 font-semibold">Required Firebase Console Checks:</h4>
            {appCheckInfo && (
              <p className="mb-2 text-sm">
                <strong>App Check:</strong> {appCheckInfo}
              </p>
            )}
            <ol className="list-inside list-decimal space-y-1 text-sm">
              <li>
                <a
                  href="https://console.firebase.google.com/project/kbe-website/authentication/providers"
                  target="_blank"
                  className="text-primary hover:underline"
                  rel="noopener"
                >
                  Check Google Provider is Enabled
                </a>
              </li>
              <li>
                <a
                  href="https://console.firebase.google.com/project/kbe-website/authentication/settings"
                  target="_blank"
                  className="text-primary hover:underline"
                  rel="noopener"
                >
                  Check Authorized Domains
                </a>
              </li>
              <li>
                <a
                  href="https://console.cloud.google.com/apis/credentials/consent?project=kbe-website"
                  target="_blank"
                  className="text-primary hover:underline"
                  rel="noopener"
                >
                  Check OAuth Consent Screen
                </a>
              </li>
              <li>
                <a
                  href="https://console.firebase.google.com/project/kbe-website/appcheck/apps"
                  target="_blank"
                  className="text-primary hover:underline"
                  rel="noopener"
                >
                  Verify App Check (reCAPTCHA Enterprise) domains include homerenrichment.com and
                  www
                </a>
              </li>
            </ol>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

