  .hover\:text-primary:hover {
    color: hsl(201 48% 25%); /* Even darker on hover in light mode */
  }

  .dark .hover\:text-primary:hover {
    color: hsl(201 48% 76%); /* Lighter on hover in dark mode */
  }
}

