location.sourceCode:
'use client';

import { addDoc, collection, serverTimestamp } from 'firebase/firestore';
import { AlertCircle, ArrowLeft, User } from 'lucide-react';
import Link from 'next/link';
import { useRouter } from 'next/navigation';
import { useState, useId } from 'react';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Checkbox } from '@/components/ui/checkbox';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { useAuth } from '@/hooks/use-auth';
import { useToast } from '@/hooks/use-toast';
import { db } from '@/lib/firebase';

interface StudentFormData {
  firstName: string;
  lastName: string;
  grade: string;
  school: string;
  dateOfBirth: string;
  medicalNotes: string;
  emergencyContact: string;
  emergencyPhone: string;
  registerForMathCounts: boolean;
}

export default function AddStudentPage() {
  const firstNameId = useId();
  const lastNameId = useId();
  const gradeId = useId();
  const schoolId = useId();
  const dateOfBirthId = useId();
  const emergencyContactId = useId();
  const emergencyPhoneId = useId();
  const medicalNotesId = useId();
  const mathcountsId = useId();
  const router = useRouter();
  const { user } = useAuth();
  const { toast } = useToast();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState<StudentFormData>({
    firstName: '',
    lastName: '',
    grade: '',
    school: '',
    dateOfBirth: '',
    medicalNotes: '',
    emergencyContact: '',
    emergencyPhone: '',
    registerForMathCounts: true, // Default to true since this is why they're here
  });

  const schools = [
    'Homer Middle School',
    'Paul Banks Elementary',
    'West Homer Elementary',
    'McNeil Canyon Elementary',
    'Chapman School',
    'Fireweed Academy',
    'Homeschool',
    'Other',
  ];

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!user) {
      toast({
        title: 'Authentication required',
        description: 'Please sign in to add a student.',
        variant: 'destructive',
      });
      return;
    }

    // Validate required fields
    if (
      !formData.firstName ||
      !formData.lastName ||
      !formData.grade ||
      !formData.school ||
      !formData.dateOfBirth
    ) {
      toast({
        title: 'Missing information',
        description: 'Please fill in all required fields.',
        variant: 'destructive',
      });
      return;
    }

    setLoading(true);

    try {
      // Add student to Firestore
      const studentData = {
        guardianId: user.uid,
        displayName: `${formData.firstName} ${formData.lastName}`,
        firstName: formData.firstName,
        lastName: formData.lastName,
        grade: parseInt(formData.grade, 10),
        school: formData.school,
        dateOfBirth: formData.dateOfBirth,
        medicalNotes: formData.medicalNotes,
        emergencyContact: formData.emergencyContact,
        emergencyPhone: formData.emergencyPhone,
        waiverStatus: 'pending',
        createdAt: serverTimestamp(),
        updatedAt: serverTimestamp(),
      };

      const docRef = await addDoc(collection(db, 'students'), studentData);

      // If registering for MathCounts, add program registration
      if (formData.registerForMathCounts) {
        await addDoc(collection(db, 'registrations'), {
          studentId: docRef.id,
          guardianId: user.uid,
          programId: 'mathcounts-2025',
          programName: 'MathCounts 2025',
          status: 'registered',
          registeredAt: serverTimestamp(),
          notes: 'Initial registration',
        });
      }

      toast({
        title: 'Student added successfully!',
        description: formData.registerForMathCounts
          ? `${formData.firstName} has been registered for MathCounts 2025.`
          : `${formData.firstName} has been added to your account.`,
      });

      // Redirect to dashboard
      router.push('/dashboard');
    } catch (error) {
      console.error('Error adding student:', error);
      toast({
        title: 'Error adding student',
        description: 'Please try again or contact support.',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto max-w-3xl px-4 py-8">
      <div className="mb-6">
        <Button variant="ghost" size="sm" asChild>
          <Link href="/dashboard">
            <ArrowLeft className="mr-2 h-4 w-4" />
            Back to Dashboard
          </Link>
        </Button>
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <User className="h-5 w-5" />
            Add Your Child
          </CardTitle>
          <CardDescription>
