  return z.array(schema).min(1, errorMessage);
}

/**
 * Common form schemas used across the application
 */
export const commonSchemas = {
  email: emailSchema,
  password: passwordSchema,
  displayName: displayNameSchema,
  url: urlSchema,
  textArea: textAreaSchema,
} as const;
